window.productTour={async init({userId:t=null,hasConsent:o=!1}){const n=window.location.hostname,e=window.location.pathname;if(o)try{const o=await fetch(`https://api.yourdomain.com/tour?domain=${n}&path=${e}`);if(!o.ok)return;const r=await o.json();if(!r?.length)return;for(let o=0;o<r.length;o++){const n=r[o],e=document.querySelector(n.selector);if(!e)continue;const i=document.createElement("div");i.className="product-tour-tooltip",i.innerHTML=n.text,i.style.cssText="\n          position: absolute;\n          background: #000;\n          color: #fff;\n          padding: 8px;\n          border-radius: 4px;\n          font-size: 14px;\n          z-index: 9999;\n        ",e.appendChild(i),fetch("https://api.yourdomain.com/tour-analytics",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({event:"step_viewed",userId:t,tourId:n.tourId,selector:n.selector,stepIndex:o+1,timestamp:(new Date).toISOString()})})}}catch(t){console.error("ProductTour error:",t)}}};